import IP2Location


class IP2Loc():
    def __init__(self, app=None):
        self.ip2loc = None
        if app:
            self.init_app(app)

    def init_app(self, app):
        self.ip2loc = IP2Location.IP2Location(app.config['IPDB_PATH'], 'SHARED_MEMORY')

    def get_city(self, ip):
        return self.ip2loc.get_city(ip)
